import {Collection} from 'fireorm';
import {Member, MemberType} from '@bike-coop/common';


@Collection('members')
export class MemberEntity implements Member {
  /** Id generated by db, same as firebase auth user uid */
  id: string;

  /** Generated initially by firebase auth user displayName */
  name: string;

  /**  */
  type: MemberType;
  email: string;
}
